import React, { useState } from 'react';
import './App.css';
import Box from './components/Box';
import VerticalDivider from './components/VerticalDivider';

function App() {
  const [clicked, setClicked] = useState(false);

  const handleClick = () => {
    setClicked(true);
    setTimeout(() => setClicked(false), 300); // Reset animation after 300ms
  };

  return (
    <div className="page">
      <Box id="box1" />
      <VerticalDivider />
      <Box id="box2" />
      <div id="box2-buttons">
        <button className={clicked ? "left-button clicked" : "left-button"} onClick={handleClick}>
          <img src="img/3a.png" alt="Left" />
        </button>
        <button className={clicked ? "right-button clicked" : "right-button"} onClick={handleClick}>
          <img src="img/3b.png" alt="Right" />
        </button>
      </div>
    </div>
  );
}

export default App;
