import React, { useState } from 'react';
import '../App.css';

function Box({ id }) {
  const [isGlowing, setIsGlowing] = useState(false);

  const handleDrag = (event) => {
    const scrollHandle = event.target;
    const scrollContainer = scrollHandle.parentElement;
    const containerHeight = scrollContainer.offsetHeight;
    const handleHeight = scrollHandle.offsetHeight;
    const containerTop = scrollContainer.getBoundingClientRect().top;
    const offsetY = event.clientY - containerTop;

    // Calculate the scroll position relative to the container
    const newScrollTop = offsetY - handleHeight / 2;

    // Ensure the scroll position stays within the container bounds
    const minY = 0;
    const maxY = containerHeight - handleHeight;
    const constrainedScrollTop = Math.max(minY, Math.min(newScrollTop, maxY));

    // Update the scroll handle position
    scrollHandle.style.top = `${constrainedScrollTop}px`;
  };


  const handleScrollHandleClick = () => {
    setIsGlowing(!isGlowing);
  };

  return (
    <div className="box" id={id}>
      {id === 'box1' && (
        <div>
          <div className="scroll1">
            <img src="img/1.png" alt="Top Image" />
            <img src="img/2.png" alt="Bottom Image" />
            <div className="scroll-handle-1"></div>
            <div className="scroll-handle-2"></div>
            <div className="scroll-handle-circle" onMouseDown={handleDragStart} onMouseMove={handleDrag} onMouseUp={handleDragEnd}></div>
          </div>
          <div className="scroll2">
            <img src="img/1.png" alt="Top Image" />
            <img src="img/2.png" alt="Bottom Image" />
            <div className={`scroll-handle-3 ${isGlowing ? 'glow-effect' : ''}`} onClick={handleScrollHandleClick}></div>
            <div className="lights-text">LIGHTS</div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Box;
