// Box.js

import React, { useState } from 'react';
import '../App.css';

function Box({ id }) {
  const [isGlowing, setIsGlowing] = useState(false);

  const handleDrag = (event) => {
    const newY = event.clientY - event.target.getBoundingClientRect().top;
    const scrollHandle = event.target;
    const containerHeight = scrollHandle.parentElement.offsetHeight;
    const handleHeight = scrollHandle.offsetHeight;
    const minY = 0;
    const maxY = containerHeight - handleHeight;
    const constrainedY = Math.max(minY, Math.min(newY, maxY));
    scrollHandle.style.top = `${constrainedY}px`;
  };

  const handleScrollHandleClick = () => {
    setIsGlowing(!isGlowing);
  };
x
  return (
    <div className="box" id={id}>
      {id === 'box1' && (
        <div>
          <div className="scroll1">
            <img src="img/1.png" alt="Top Image" />
            <img src="img/2.png" alt="Bottom Image" />
            <div className="scroll-handle-1"></div>
            <div className="scroll-handle-2"></div>
            <div className={`scroll-handle-circle ${isGlowing ? 'glow-effect' : ''}`} onMouseDown={handleDrag}></div>
          </div>
          <div className="scroll2">
            <img src="img/1.png" alt="Top Image" />
            <img src="img/2.png" alt="Bottom Image" />
            <div className="scroll-handle-3"></div>
            <div className="lights-text" onClick={handleScrollHandleClick}>LIGHTS</div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Box;
