import React, { useState } from 'react';
import '../App.css';

function Box({ id, minHeight, maxHeight }) {
  const [isDragging, setIsDragging] = useState(false);
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleMouseDown = () => {
    setIsDragging(true);
  };

  const handleMouseMove = (event) => {
    if (isDragging) {
      const newY = event.clientY - event.target.getBoundingClientRect().top;
      const containerHeight = maxHeight - minHeight;
      const scrollHandleHeight = 20; // Adjust this according to your scroll handle height
      const newScrollPosition = Math.max(minHeight, Math.min(newY - scrollHandleHeight / 2, maxHeight - scrollHandleHeight));
      setScrollPosition(newScrollPosition);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

